/*
 * @Descripttion: 列表页面
 * @Author: zhaokh
 * @Date: 2020-02-02 17:11:34
 */
import React, { useState } from "react";
import Head from "next/head";
import { Row, Col, List, Icon, Breadcrumb } from "antd";
import axios from "axios";
import Header from "../components/Header";
import Author from "../components/Author";
import Footer from "../components/Footer";
import API_URL from "../constant/api";

const BlogList = props => {
  const { list = {} } = props;
  const [myList, setLMylist] = useState(list);

  return (
    <div>
      <Head>
        <title>List</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>
      <Header />

      <Row className="common-main" type="flex" justify="center">
        <Col className="common-left" xs={24} sm={24} md={16} lg={18} xl={14}>
          <Breadcrumb>
            <Breadcrumb.Item>
              <a href="/">首页</a>
            </Breadcrumb.Item>
            <Breadcrumb.Item>视频教程</Breadcrumb.Item>
          </Breadcrumb>
          <List
            header={<div>最新日志</div>}
            itemLayout="vertical"
            dataSource={myList}
            renderItem={item => (
              <List.Item>
                <div className="list-title">{item.title}</div>
                <div className="list-icon">
                  <span>
                    <Icon type="calendar" /> 2019-06-28
                  </span>
                  <span>
                    <Icon type="folder" /> 视频教程
                  </span>
                  <span>
                    <Icon type="fire" /> 5498人
                  </span>
                </div>
                <div className="list-context">{item.context}</div>
              </List.Item>
            )}
          />
        </Col>
        <Col className="common-right" xs={0} sm={0} md={7} lg={5} xl={4}>
          <Author />
        </Col>
        <Footer />
      </Row>
    </div>
  );
};

BlogList.getInitialProps = async ctx => {
  const { id = "" } = ctx.query;
  const { data: list = [] } = await axios.get(API_URL.getList + id);
  return { list };
};

export default BlogList;
