/*
 * @Descripttion:首页页面
 * @Author: zhaokh
 * @Date: 2020-01-29 14:34:29
 */
import React, { useState } from "react";
import Head from "next/head";
import Link from "next/link";
import axios from "axios";
import { Row, Col, List, Icon } from "antd";
import "../static/style/pages/index.css";
import Header from "../components/Header";
import Author from "../components/Author";
import Footer from "../components/Footer";

const Home = props => {
  const [myList, setLMylist] = useState(props["0"]);

  return (
    <div>
      <Head>
        <title>Home</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>

      <Header />

      <Row className="common-main" type="flex" justify="center">
        <Col className="common-left" xs={24} sm={24} md={16} lg={18} xl={14}>
          <List
            header={<div>最新日志</div>}
            itemLayout="vertical"
            dataSource={myList}
            renderItem={item => (
              <List.Item>
                <div className="list-title">
                  <Link href={{ pathname: "/detail", query: { id: item.id } }}>
                    <a>{item.title}</a>
                  </Link>
                </div>
                <div className="list-icon">
                  <span>
                    <Icon type="calendar" /> {item.releaseTime}
                  </span>
                  <span>
                    <Icon type="folder" /> {item.intruduction}
                  </span>
                  <span>
                    <Icon type="fire" /> {`${item.browseTimes}人`}
                  </span>
                </div>
                <div className="list-context">{item.content}</div>
              </List.Item>
            )}
          />
        </Col>
        <Col className="common-right" xs={0} sm={0} md={7} lg={5} xl={4}>
          <Author />
        </Col>
        <Footer />
      </Row>
    </div>
  );
};

Home.getInitialProps = async () => {
  const { data: articles = [] } = await axios.get(
    "http://localhost:7001/fontend/list"
  );
  return [articles];
};

export default Home;
